mapper.select:
SELECT

FROM goods_category


        AND category_level = #{categoryLevel,jdbcType=INTEGER}
        AND parent_id = #{parentId,jdbcType=BIGINT}

    AND is_deleted = 0

ORDER BY category_rank DESC

SELECT COUNT(*)
FROM goods_category


        AND path = #{parentId,jdbcType=VARCHAR}
        AND category_level = #{categoryLevel,jdbcType=INTEGER}

    AND is_deleted = 0

SELECT

FROM goods_category
WHERE category_name = #{categoryName,jdbcType=VARCHAR}
AND category_level = #{categoryLevel,jdbcType=TINYINT}
AND is_deleted = 0 LIMIT 1;

SELECT

FROM goods_category
WHERE parent_id = #{parentIds,jdbcType=BIGINT}
AND category_level = #{categoryLevel, jdbcType=TINYINT}
AND is_deleted = 0
ORDER BY category_rank DESC

    LIMIT #{number}

mapper.update:
update goods_category
SET is_deleted = 1
WHERE id = #{categoryId,jdbcType=BIGINT}

UPDATE goods_category
SET is_deleted = 1
WHERE id IN

#{id}
