SELECT 
FROM goods_info
JOIN goods_category gc on gc.goods_category_id = goods_info.goods_category_id
WHERE gc.path LIKE CONCAT(#{path,jdbcType=VARCHAR}, '.%') OR
    gc.path = #{path,jdbcType=VARCHAR}
LIMIT #{start}, #{limit};

    SELECT 
