SELECT goods_info.goods_id, goods_info.goods_name, goods_info.goods_brand_id, goods_brand.goods_brand_name, goods_info.goods_category_id, goods_category.category_name, goods_info.goods_cover_img, goods_info.selling_price, COUNT(goods_info.goods_id) AS 'accuracy' FROM goods_info JOIN goods_has_tag ON goods_info.goods_id = goods_has_tag.goods_id JOIN goods_tag ON goods_has_tag.tag_id = goods_tag.goods_tag_id JOIN goods_brand ON goods_info.goods_brand_id = goods_brand.goods_brand_id JOIN goods_category ON goods_info.goods_category_id = goods_category.goods_category_id
goods_tag.tag_name = #{keyword,jdbcType=VARCHAR}
GROUP BY goods_info.goods_id ORDER BY accuracy DESC;
